import React from "react";
import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";
import "./Skills.css";

const Skills = ({ resumeData }) => {
  const [ref, inView] = useInView({
    threshold: 0.2,
    triggerOnce: true,
  });

  const skillCategories = {
    "Frontend Development": [
      { name: "React.js", icon: "fab fa-react" },
      { name: "JavaScript (ES6+)", icon: "fab fa-js" },
      { name: "TypeScript", icon: "fas fa-code" },
      { name: "HTML5/CSS3", icon: "fab fa-html5" },
      { name: "Material UI", icon: "fas fa-palette" },
      { name: "Redux", icon: "fas fa-layer-group" },
    ],
    "Backend Development": [
      { name: "Node.js", icon: "fab fa-node-js" },
      { name: "Python", icon: "fab fa-python" },
      { name: "Java", icon: "fab fa-java" },
      { name: "Express", icon: "fas fa-server" },
      { name: "RESTful APIs", icon: "fas fa-plug" },
      { name: "GraphQL", icon: "fas fa-project-diagram" },
    ],
    "Database & Tools": [
      { name: "MongoDB", icon: "fas fa-database" },
      { name: "PostgreSQL", icon: "fas fa-database" },
      { name: "Git", icon: "fab fa-git-alt" },
      { name: "Docker", icon: "fab fa-docker" },
      { name: "AWS", icon: "fab fa-aws" },
      { name: "Jenkins", icon: "fab fa-jenkins" },
    ],
    "Other Skills": [
      { name: "Agile/Scrum", icon: "fas fa-users-cog" },
      { name: "CI/CD", icon: "fas fa-infinity" },
      { name: "Unit Testing", icon: "fas fa-vial" },
      { name: "Problem Solving", icon: "fas fa-puzzle-piece" },
      { name: "Team Leadership", icon: "fas fa-users" },
      { name: "Technical Writing", icon: "fas fa-file-alt" },
    ],
  };

  return (
    <section id="skills" className="skills-section">
      <motion.div
        ref={ref}
        className="skills-container"
        initial={{ opacity: 0, y: 20 }}
        animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
        transition={{ duration: 0.6 }}
      >
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <span className="section-number">02.</span> Skills & Technologies
        </motion.h2>
        <div className="skills-grid">
          {Object.entries(skillCategories).map(([category, skills], idx) => (
            <motion.div
              key={category}
              className="skill-card"
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
              transition={{ duration: 0.6, delay: 0.2 + idx * 0.1 }}
            >
              <h3>{category}</h3>
              <ul className="skill-list">
                {skills.map((skill, index) => (
                  <motion.li
                    key={skill.name}
                    className="skill-item"
                    initial={{ opacity: 0, x: -20 }}
                    animate={
                      inView ? { opacity: 1, x: 0 } : { opacity: 0, x: -20 }
                    }
                    transition={{ duration: 0.6, delay: 0.3 + index * 0.1 }}
                  >
                    <i className={skill.icon}></i>
                    {skill.name}
                  </motion.li>
                ))}
              </ul>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </section>
  );
};

export default Skills;
