import React from "react";
import { motion } from "framer-motion";
import { useInView } from "react-intersection-observer";
import "./Work.css";

const Work = ({ resumeData }) => {
  const [ref, inView] = useInView({
    threshold: 0.2,
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        duration: 0.6,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
      },
    },
  };

  return (
    <section id="work" className="work-section">
      <div className="work-background"></div>
      <motion.div
        ref={ref}
        className="work-container"
        variants={containerVariants}
        initial="hidden"
        animate={inView ? "visible" : "hidden"}
      >
        <motion.div className="work-content" variants={itemVariants}>
          <motion.h2 variants={itemVariants}>Work Experience</motion.h2>
          <div className="work-grid">
            {resumeData.work.map((job, index) => (
              <motion.div
                key={index}
                className="work-item"
                variants={itemVariants}
                whileHover={{ scale: 1.02 }}
              >
                <div className="work-date">
                  {job.MonthOfStarting} {job.YearOfStarting} -{" "}
                  {job.MonthOfLeaving} {job.YearOfLeaving}
                </div>
                <div className="work-content">
                  <h4>{job.CompanyName}</h4>
                  <p className="work-position">{job.position}</p>
                  <p className="work-description">{job.Description}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default Work;
